def test_load_magazine() -> None:
    """It returns a valid Document for a conference talk."""
    url = "https://www.churchofjesuschrist.org/study/liahona/2023/09/04-brothers-and-sisters-in-the-lord?lang=eng"
    result = load_magazine(url, html)
    assert len(result.page_content) > 0
    assert result.metadata["url"] == url
    assert result.metadata["title"] == "Brothers and Sisters in the Lord"
    assert "Traveling from Capernaum," in result.page_content
